import React, { Component } from "react";
import Modal from 'react-modal';
import './routine.css';

  const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)',
      border                : '1px solid black'
    }
  };

  Modal.setAppElement('#root')

  class routine extends Component {
    constructor() {
      super();

      this.state = {
        modalIsOpen: false
      };

      this.openModal = this.openModal.bind(this);
      this.afterOpenModal = this.afterOpenModal.bind(this);
      this.closeModal = this.closeModal.bind(this);
    }

    openModal() {
      this.setState({modalIsOpen: true});
    }

    afterOpenModal() {
      // references are now sync'd and can be accessed.
      this.subtitle.style.color = '#f00';
    }

    closeModal() {
      this.setState({modalIsOpen: false});
    }

    render() {
      return (
        <div>
          <button id="new" className="btn btn-outline-dark" onClick={this.openModal}>ADD NEW EXERCISE</button>
          <Modal
            isOpen={this.state.modalIsOpen}
            onAfterOpen={this.afterOpenModal}
            onRequestClose={this.closeModal}
            style={customStyles}
            contentLabel="Example Modal"
          >

            <h2 ref={subtitle => this.subtitle = subtitle}>Something New, eh?</h2>
            <form>
            <h8>Exercise Description:</h8>
              <input type="text" id="newExercise"/><br/>         
              <h8>Weight:</h8>
              <input type="number" id="weight"/>
              <h8>Sets:</h8>
              <select className="custom-select sets" id="sets" name="bodyChoice">
                <option value="3">3</option>
                <option value="5">1</option>
              </select><h8>Rep's / Set:</h8><input type="text" className="reps" id="reps"/>
                  <button className="btn btn-outline-dark">SUBMIT</button>
              {/* <button className="btn btn-outline-dark" onClick={this.closeModal}>CANCEL</button> */}
            </form>
          </Modal>
        </div>
      );
    }
  }

export default routine;
